import { useState, useEffect, useRef } from 'react';
import { SpeakerWaveIcon, SpeakerXMarkIcon } from '@heroicons/react/24/outline';

export default function AudioPlayer({ currentStoryIndex, currentType }) {
  const [isMuted, setIsMuted] = useState(false);
  const audioRef = useRef(null);

  const songs = [
    {
      url: '/audio/song1.mp3',
      startAt: 0,
      endAt: 1,
      title: "Perfect - Ed Sheeran"
    },
    {
      url: '/audio/song4.mp3', // Bizim Şarkımız için özel müzik
      type: 'music',
      title: "Bizim Şarkımız"
    },
    {
      url: '/audio/song2.mp3',
      startAt: 3,
      endAt: 5,
      title: "All of Me - John Legend"
    },
    {
      url: '/audio/song3.mp3',
      startAt: 6,
      endAt: 8,
      title: "Can't Help Falling in Love - Elvis Presley"
    }
  ];

  useEffect(() => {
    let currentSong;
    
    // Eğer müzik kartındaysak, özel şarkıyı çal
    if (currentType === 'music') {
      currentSong = songs.find(song => song.type === 'music');
    } else {
      // Diğer kartlar için normal şarkı listesini kullan
      currentSong = songs.find(
        song => song.startAt !== undefined && 
        currentStoryIndex >= song.startAt && 
        currentStoryIndex <= song.endAt
      );
    }

    if (currentSong && audioRef.current) {
      audioRef.current.src = currentSong.url;
      if (!isMuted) {
        audioRef.current.play().catch(error => {
          console.log("Ses oynatma hatası:", error);
        });
      }
    }
  }, [currentStoryIndex, currentType, isMuted]);

  // Şarkı bilgisini göstermek için
  const currentSong = currentType === 'music' 
    ? songs.find(song => song.type === 'music')
    : songs.find(
        song => song.startAt !== undefined && 
        currentStoryIndex >= song.startAt && 
        currentStoryIndex <= song.endAt
      );

  return (
    <div className="absolute bottom-16 right-4 z-50 flex items-center gap-4">
      {currentSong && (
        <div className="text-white text-sm bg-black bg-opacity-50 px-3 py-1 rounded-full">
          {currentSong.title}
        </div>
      )}
      <button
        onClick={() => setIsMuted(!isMuted)}
        className="p-2 rounded-full bg-black bg-opacity-50 hover:bg-opacity-70 transition-all"
      >
        {isMuted ? (
          <SpeakerXMarkIcon className="h-6 w-6 text-white" />
        ) : (
          <SpeakerWaveIcon className="h-6 w-6 text-white" />
        )}
      </button>
      <audio ref={audioRef} loop={true} muted={isMuted} />
    </div>
  );
}
