import Story from './components/Story';

function App() {
  const stories = [
    {
      type: 'message',
      title: 'Sevgilim',
      message:
        'Seninle geçirdiğim her an için şanslıyım. Bu özel günde sana olan sevgimi anlatmak istiyorum...',
    },
    {
      type: 'photo',
      image: 'https://picsum.photos/800/600?random=1',
      title: 'İlk Buluşmamız',
      date: '14 Şubat 2023',
      description:
        'O gün hayatımın en güzel günlerinden biri olacağını bilmiyordum...',
    },
    {
      type: 'music',
      songs: [
        {
          title: 'Perfect',
          artist: 'Ed Sheeran',
          albumCover: 'https://picsum.photos/200/200?random=2',
        },
        {
          title: 'All of Me',
          artist: 'John Legend',
          albumCover: 'https://picsum.photos/200/200?random=3',
        },
      ],
    },
    {
      type: 'photo',
      image: 'https://picsum.photos/800/600?random=4',
      title: 'İlk Tatilimiz',
      date: '15 Temmuz 2023',
      description:
        'Birlikte çıktığımız ilk tatil, unutulmaz anılarla doluydu...',
    },
    {
      type: 'message',
      title: 'En Güzel Anlarımız',
      message:
        'Seninle paylaştığımız her an, hayatıma kattığın her renk için teşekkür ederim...',
    },
    {
      type: 'photo',
      image: 'https://picsum.photos/800/600?random=5',
      title: 'Doğum Günün',
      date: '1 Eylül 2023',
      description: 'Senin mutluluğun benim en büyük mutluluğum...',
    },
    {
      type: 'music',
      songs: [
        {
          title: "Can't Help Falling in Love",
          artist: 'Elvis Presley',
          albumCover: 'https://picsum.photos/200/200?random=6',
        },
        {
          title: 'Thinking Out Loud',
          artist: 'Ed Sheeran',
          albumCover: 'https://picsum.photos/200/200?random=7',
        },
      ],
    },
    {
      type: 'photo',
      image: 'https://picsum.photos/800/600?random=8',
      title: 'Yılbaşı Gecesi',
      date: '31 Aralık 2023',
      description: 'Yeni yıla seninle girmenin mutluluğu...',
    },
    {
      type: 'message',
      title: 'Seni Seviyorum',
      message:
        'Her geçen gün sana olan sevgim daha da artıyor. Nice senelere birlikte...',
    },
  ];

  return (
    <div className="min-h-screen bg-black">
      <Story stories={stories} />
    </div>
  );
}

export default App;
