import Story from './components/Story';

function App() {
  const stories = [
    {
      type: 'message',
      title: 'Sevgilim',
      message: 'Seninle geçirdiğim her an için şanslıyım. Bu özel günde sana olan sevgimi anlatmak istiyorum...'
    },
    {
      type: 'photo',
      image: 'https://picsum.photos/800/600', // Test için örnek resim
      title: 'İlk Buluşmamız',
      date: '1 Ocak 2023',
      description: 'O gün hayatımın en güzel günlerinden biri olacağını bilmiyordum...'
    },
    {
      type: 'music',
      songs: [
        {
          title: 'Aşk Şarkısı',
          artist: 'Sanatçı',
          albumCover: 'https://picsum.photos/200/200' // Test için örnek resim
        },
        {
          title: 'Bizim Şarkımız',
          artist: 'Sanatçı',
          albumCover: 'https://picsum.photos/200/200' // Test için örnek resim
        }
      ]
    },
    // Daha fazla hikaye ekleyin...
    {
      type: 'message',
      title: 'Seni Seviyorum',
      message: 'Nice senelere birlikte...'
    }
  ];

  return (
    <div className="min-h-screen bg-black">
      <Story stories={stories} />
    </div>
  );
}

export default App;
