import { useState, useEffect, useRef } from 'react';
import { SpeakerWaveIcon, SpeakerXMarkIcon } from '@heroicons/react/24/outline';

export default function AudioPlayer({ currentStoryIndex }) {
  const [isMuted, setIsMuted] = useState(false);
  const audioRef = useRef(null);

  const songs = [
    {
      url: '/audio/song1.mp3',
      startAt: 0, // İlk hikaye
      endAt: 2, // 3. hikayeye kadar
      title: "Perfect - Ed Sheeran" // Opsiyonel: şarkı bilgisi
    },
    {
      url: '/audio/song2.mp3',
      startAt: 3,
      endAt: 5,
      title: "All of Me - John Legend"
    },
    {
      url: '/audio/song3.mp3',
      startAt: 6,
      endAt: 8,
      title: "Can't Help Falling in Love - Elvis Presley"
    }
  ];

  useEffect(() => {
    const currentSong = songs.find(
      (song) => currentStoryIndex >= song.startAt && currentStoryIndex <= song.endAt
    );

    if (currentSong && audioRef.current) {
      audioRef.current.src = currentSong.url;
      if (!isMuted) {
        audioRef.current.play().catch(error => {
          console.log("Ses oynatma hatası:", error);
        });
      }
    }
  }, [currentStoryIndex, isMuted]);

  // Şarkı bilgisini göstermek için
  const currentSong = songs.find(
    (song) => currentStoryIndex >= song.startAt && currentStoryIndex <= song.endAt
  );

  return (
    <div className="absolute bottom-16 right-4 z-50 flex items-center gap-4">
      {currentSong && (
        <div className="text-white text-sm bg-black bg-opacity-50 px-3 py-1 rounded-full">
          {currentSong.title}
        </div>
      )}
      <button
        onClick={() => setIsMuted(!isMuted)}
        className="p-2 rounded-full bg-black bg-opacity-50 hover:bg-opacity-70 transition-all"
      >
        {isMuted ? (
          <SpeakerXMarkIcon className="h-6 w-6 text-white" />
        ) : (
          <SpeakerWaveIcon className="h-6 w-6 text-white" />
        )}
      </button>
      <audio ref={audioRef} loop={true} muted={isMuted} />
    </div>
  );
}
