import { useState, useEffect, useRef } from 'react';
import { PlayIcon, PauseIcon } from '@heroicons/react/24/outline';

export default function AudioPlayer({ currentStoryIndex, currentType }) {
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);

  const songs = [
    {
      url: '/audio/song1.mp3', // Giriş mesajı için romantik bir müzik
      type: 'message',
      title: "Romantik Giriş"
    },
    {
      url: '/audio/song2.mp3', // İlk buluşma anısı için heyecanlı bir müzik
      type: 'photo',
      title: "İlk Buluşma"
    },
    {
      url: '/audio/song3.mp3', // Film anısı için sinematik bir müzik
      type: 'memory',
      memoryTitle: 'İlk İzlediğimiz Film'
    },
    {
      url: '/audio/song4.mp3', // Bizim özel şarkımız
      type: 'music',
      title: "Bizim Şarkımız"
    },
    {
      url: '/audio/song5.mp3', // Kahve molası anısı için rahat bir müzik
      type: 'memory',
      memoryTitle: 'İlk Kahve Molası'
    },
    {
      url: '/audio/song6.mp3', // Tatil anısı için neşeli bir müzik
      type: 'memory',
      memoryTitle: 'İlk Tatilimiz'
    }
  ];

  useEffect(() => {
    let currentSong;
    
    if (currentType === 'music') {
      // Müzik kartı için özel şarkı (song4.mp3)
      currentSong = songs.find(song => song.type === 'music');
    } else if (currentType === 'memory') {
      // Memory kartları için özel müzikler
      currentSong = songs.find(
        song => song.type === 'memory' && 
        song.memoryTitle === stories[currentStoryIndex].title
      );
    } else {
      // Diğer kartlar için genel müzikler
      currentSong = songs.find(song => song.type === currentType);
    }

    // Eğer o kart için özel müzik yoksa varsayılan müziği çal
    if (!currentSong) {
      currentSong = songs[0];
    }

    if (currentSong && audioRef.current) {
      audioRef.current.src = currentSong.url;
      if (isPlaying) {
        audioRef.current.play().catch(error => {
          console.log("Ses oynatma hatası:", error);
        });
      }
    }
  }, [currentStoryIndex, currentType, isPlaying]);

  const togglePlay = () => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play().catch(error => {
          console.log("Ses oynatma hatası:", error);
        });
      }
      setIsPlaying(!isPlaying);
    }
  };

  return (
    <div className="absolute bottom-8 right-8 z-50">
      <button
        onClick={togglePlay}
        className="px-6 py-3 rounded-full bg-valentine-500 hover:bg-valentine-600 text-white flex items-center gap-2 shadow-lg transition-all"
      >
        {isPlaying ? (
          <>
            <PauseIcon className="h-6 w-6" />
            <span>Müziği Durdur</span>
          </>
        ) : (
          <>
            <PlayIcon className="h-6 w-6" />
            <span>Müziği Başlat</span>
          </>
        )}
      </button>
      <audio ref={audioRef} loop={true} />
    </div>
  );
}
