import Story from './components/Story';

function App() {
  const stories = [
    {
      type: 'message',
      title: 'Sevgilim',
      message: 'Seninle geçirdiğim her an için şanslıyım. Bu özel günde seninle olan anılarımızı paylaşmak istiyorum...'
    },
    {
      type: 'photo',
      image: 'https://picsum.photos/800/600?random=1',
      title: 'İlk Buluşmamız',
      date: '14 Şubat 2023',
      description: 'O gün hayatımın en güzel günlerinden biri olacağını bilmiyordum...'
    },
    {
      type: 'memory',
      title: 'İlk İzlediğimiz Film',
      date: '20 Şubat 2023',
      icon: '🎬',
      movie: 'La La Land',
      description: 'Sinemada yan yana oturup, aynı anları paylaştığımız ilk film. Patlamış mısırı paylaşırken gizlice sana bakıyordum.',
      location: 'Cinemaximum'
    },
    {
      type: 'memory',
      title: 'İlk Kahve Molası',
      date: '1 Mart 2023',
      icon: '☕',
      place: 'Starbucks',
      description: 'Sen sıcak çikolata, ben americano... Saatlerce konuşup birbirimizi tanıdığımız o güzel öğleden sonra.',
      order: 'Senin: Sıcak Çikolata\nBenim: Americano'
    },
    {
      type: 'photo',
      image: 'https://picsum.photos/800/600?random=4',
      title: 'İlk Tatilimiz',
      date: '15 Temmuz 2023',
      description: 'Birlikte çıktığımız ilk tatil, unutulmaz anılarla doluydu...'
    },
    {
      type: 'memory',
      title: 'İlk Yemek Denemem',
      date: '5 Ağustos 2023',
      icon: '🍝',
      dish: 'Makarna Carbonara',
      description: 'Senin için ilk kez yemek yapmaya çalıştığımda mutfağı dağıtmıştım. Ama sen yine de gülümseyerek "Çok güzel olmuş" dedin.',
      result: 'Biraz tuzlu olmuştu 😅'
    },
    {
      type: 'music',
      songs: [
        {
          title: "Can't Help Falling in Love",
          artist: 'Elvis Presley',
          albumCover: 'https://picsum.photos/200/200?random=6'
        },
        {
          title: 'Perfect',
          artist: 'Ed Sheeran',
          albumCover: 'https://picsum.photos/200/200?random=7'
        }
      ]
    },
    {
      type: 'memory',
      title: 'İlk Sürprizin',
      date: '15 Eylül 2023',
      icon: '🎁',
      gift: 'El Yapımı Albüm',
      description: 'Doğum günümde bana hazırladığın o el yapımı albüm... Her sayfasında farklı bir anımız, her köşesinde ayrı bir mutluluk vardı.',
      reaction: 'Gözlerim dolmuştu 🥺'
    },
    {
      type: 'memory',
      title: 'İlk Dans',
      date: '31 Aralık 2023',
      icon: '💃',
      song: 'Perfect - Ed Sheeran',
      description: 'Yılbaşı partisinde, herkesin içinde ilk dansımız. O an dünya durmuş, sadece biz vardık.',
      moment: 'Slow müzik başladığında kulağıma fısıldadığın o söz...'
    },
    {
      type: 'message',
      title: 'Seni Seviyorum',
      message: 'Her geçen gün sana olan sevgim daha da artıyor. Seninle yaşadığım her an, her anı benim için çok değerli. Nice güzel anılara... ❤️'
    }
  ];

  return (
    <div className="min-h-screen bg-black">
      <Story stories={stories} />
    </div>
  );
}

export default App;
